targets.expr:
sum by (http_route, http_request_method) (
  increase(
    http_server_request_duration_seconds_count{
      service_name="$service", 
      http_response_status_code=~"2..|3.."
    }[$__range]
  )
)

sum by (http_route, http_request_method) (
  increase(
    http_server_request_duration_seconds_count{
      service_name="$service", 
      http_response_status_code=~"4..|5.."
    }[$__range]
  )
)

sum by (http_route, http_request_method) (
  increase (
    http_server_request_duration_seconds_sum{service_name="$service"}[$__range]
  )
)
/
sum by (http_route, http_request_method) (
  increase (
    http_server_request_duration_seconds_count{service_name="$service"}[$__range]
  )
)
